let unlock = true;

//=================
//ActionsOnHash
if (location.hash) {
	const hsh = location.hash.replace('#', '');
	if (document.querySelector('.popup_' + hsh)) {
		popup_open(hsh);
	} else if (document.querySelector('div.' + hsh)) {
		_goto(document.querySelector('.' + hsh), 500, '');
	}
}
//=================
//BodyLock
function body_lock(delay) {
	let body = document.querySelector("body");
	if (body.classList.contains('_lock')) {
		body_lock_remove(delay);
	} else {
		body_lock_add(delay);
	}
}
function body_lock_remove(delay) {
	let body = document.querySelector("body");
	if (unlock) {
		let lock_padding = document.querySelectorAll("._lp");
		setTimeout(() => {
			for (let index = 0; index < lock_padding.length; index++) {
				const el = lock_padding[index];
				el.style.paddingRight = '0px';
			}
			body.style.paddingRight = '0px';
			body.classList.remove("_lock");
		}, delay);

		unlock = false;
		setTimeout(function () {
			unlock = true;
		}, delay);
	}
}
function body_lock_add(delay) {
	let body = document.querySelector("body");
	if (unlock) {
		let lock_padding = document.querySelectorAll("._lp");
		for (let index = 0; index < lock_padding.length; index++) {
			const el = lock_padding[index];
			el.style.paddingRight = window.innerWidth - document.querySelector('.wrapper').offsetWidth + 'px';
		}
		body.style.paddingRight = window.innerWidth - document.querySelector('.wrapper').offsetWidth + 'px';
		body.classList.add("_lock");

		unlock = false;
		setTimeout(function () {
			unlock = true;
		}, delay);
	}
}

//=================
//Popups
let popup_link = document.querySelectorAll('._popup-link');
let popups = document.querySelectorAll('.popup');
for (let index = 0; index < popup_link.length; index++) {
	const el = popup_link[index];
	el.addEventListener('click', function (e) {
		if (unlock) {
			let item = el.getAttribute('href').replace('#', '');
			let video = el.getAttribute('data-video');
			popup_open(item, video);
		}
		e.preventDefault();
	})
}
for (let index = 0; index < popups.length; index++) {
	const popup = popups[index];
	popup.addEventListener("click", function (e) {
		if (!e.target.closest('.popup__body')) {
			popup_close(e.target.closest('.popup'));
		}
	});
}
function popup_open(item, video = '') {
	let activePopup = document.querySelectorAll('.popup._active');
	if (activePopup.length > 0) {
		popup_close('', false);
	}
	let curent_popup = document.querySelector('.popup_' + item);
	if (curent_popup && unlock) {
		if (video != '' && video != null) {
			let popup_video = document.querySelector('.popup_video');
			popup_video.querySelector('.popup__video').innerHTML = '<iframe src="https://www.youtube.com/embed/' + video + '?autoplay=1"  allow="autoplay; encrypted-media" allowfullscreen></iframe>';
		}
		if (!document.querySelector('.menu__body._active')) {
			body_lock_add(500);
		}
		curent_popup.classList.add('_active');
		history.pushState('', '', '#' + item);
	}
}
function popup_close(item, bodyUnlock = true) {
	if (unlock) {
		if (!item) {
			for (let index = 0; index < popups.length; index++) {
				const popup = popups[index];
				let video = popup.querySelector('.popup__video');
				if (video) {
					video.innerHTML = '';
				}
				popup.classList.remove('_active');
			}
		} else {
			let video = item.querySelector('.popup__video');
			if (video) {
				video.innerHTML = '';
			}
			item.classList.remove('_active');
		}
		if (!document.querySelector('.menu__body._active') && bodyUnlock) {
			body_lock_remove(500);
		}
		history.pushState('', '', window.location.href.split('#')[0]);
	}
}
let popup_close_icon = document.querySelectorAll('.popup__close,._popup-close');
if (popup_close_icon) {
	for (let index = 0; index < popup_close_icon.length; index++) {
		const el = popup_close_icon[index];
		el.addEventListener('click', function () {
			popup_close(el.closest('.popup'));
		})
	}
}
document.addEventListener('keydown', function (e) {
	if (e.code === 'Escape') {
		popup_close();
	}
});
function mobileMenu() {
	var _height = 'auto';
	var _noHover = false;
	var width = $(window).width();
	if(width<769){
		 var left = $('#top_menu > ul').css('left');
		 if(parseInt(left)<0){
			  $('#top_menu > ul').css('left','-'+width+'px');
		 }
		 $('#top_menu').removeClass('desktop').addClass('mobile');
		 ///
		 $('#top_menu .navbar-toggle').unbind('click').click(function(){
			  $(this).find('i').toggle();
			  if($(this).find('.fa-bars:visible').length){
					$('#top_menu > ul').stop().slideUp(); 
					$('#top_menu').css('height','auto');  
			  }else{
					
					$('#top_menu > ul').stop().slideDown(function(){
						 $('#top_menu').css('height',_height+'px');
					});                   
			  }
		 });
		 $('li .btn-next').click(function(){
			  var parent = $(this).parents('ul');
			  var height = $(this).parent().find('>ul').innerHeight()+$('#top_menu .navbar').innerHeight();
			  if($(parent).hasClass('sub_menu')){                
					$(this).closest('li').addClass('active').parent().stop().animate({'left':'0'},500);
			  }else{
					$(this).closest('li').addClass('active').parent().stop().animate({'left':'-'+width+'px'},500);
			  }
			  $('#top_menu').css('height',height+'px');
			  _height = height;
			  $('#top_menu .fa-chevron-left').show();  
			  var title = $(this).closest('li').find('>span').html();
			  if(!title){
					title = $(this).closest('li').find('>a').html();
			  }
			  $('#top_menu #nav_category').html(title);  

		 });
		 $('.fa-chevron-left, #nav_category').click(function(){
			  var active = $('#top_menu li.active:last');
			  var parent = $(active).parent();
			  var height = $(parent).innerHeight()+$('#top_menu .navbar').innerHeight();
			  var title = $(parent).closest('li.active').find('>span').html();
			  if(!title){
					title = $(parent).closest('li.active').find('>a').html();
			  }
			  if(!title){
					title = $('#top_menu #nav_category').attr('data-title');
					$('#top_menu .fa-chevron-left').hide();
			  }
			  if($(parent).hasClass('sub_menu')){                
					$(parent).stop().animate({'left':'100%'},500,function(){
						 $(active).removeClass('active');
						 $('#top_menu').css('height',height+'px');
						 _height = height;
					});
			  }else{
					$(parent).stop().animate({'left':'0px'},500,function(){
						 $(active).removeClass('active');
						 $('#top_menu').css('height','auto');
						 _height = 'auto';
					});
			  }
			  $('#top_menu #nav_category').html(title); 
		 });
	}else{
		 $('#top_menu').removeClass('mobile').addClass('desktop').removeAttr('style');
		 $('#top_menu li').removeClass('active');
		 $('#top_menu ul').removeAttr('style');
		 $('#top_menu .navbar .btn i').show();
		 $('#top_menu .navbar span:first, #top_menu .navbar .btn i:last').hide();
		 $('#top_menu #nav_category').html($('#top_menu #nav_category').attr('data-title'));        
		 ///
		 $('#top_menu li').hover(
			  function(){
					if($(this).hasClass('all1')){
						 var obj = $(this).parent().find('>li:not(.all1):visible:last');                                      
					}else{
						 var obj = $(this);                    
					}
					$('#top_menu li').removeClass('active');  
					if(_noHover) return;
					$(obj).addClass('active').find('.sub_menu').find('li:first').addClass('active');  
					$(obj).parents('li').addClass('active');
					var width = $('#top_menu').innerWidth() - ($('#top_menu li.active .level2').innerWidth()+$('#top_menu li.active .level3').innerWidth());
					$('#top_menu .level4').css('width',width+'px');
			  },function(){
					if(_noHover) return;
					if(!$(this).hasClass('all1')){
						 //$('#top_menu li').removeClass('active');
					}
			  }
		 );
		 $('.show_all').click(function(){
			  _noHover = true;
			  var type = $(this).attr('data-type');
			  if(type==1){
				  $(this).parent().find('> li.hide_all').show();
				  $(this).html('<span>'+$(this).attr('data-hide')+'</span>');
				  $(this).attr('data-type',0); 
				  var height = ((($(this).index()+1)/$(this).attr('data-item'))+1)*40;
				  if(height>$('#top_menu li.active .level2').innerHeight()){
						 $('#top_menu li.active .level2').css('height',height+'px');
				  }
			  }else{
				  $(this).parent().find('> li.hide_all').hide();
				  $(this).html('<span>'+$(this).attr('data-show')+'</span>');
				  $(this).attr('data-type',1);
			  }
			  setTimeout(function(){
					_noHover = false;
			  },500);
		 });        
	}
}
$(document).ready(mobileMenu);
$(window).resize(mobileMenu);